import React, { useEffect, useState } from "react";
import { Card, CardContent } from "../src/components/ui/card.js";
import { Chart, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar } from "react-chartjs-2";
import { Table, TableHead, TableRow, TableCell, TableBody } from "../src/components/ui/table.js";

// ثبت مقیاس‌های مورد نیاز
Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    // Fetch dashboard data
    fetch("http://localhost:8000/dashboard")
      .then((res) => res.json())
      .then((data) => setDashboardData(data))
      .catch((err) => console.error("Failed to fetch dashboard data:", err));

    // Fetch alerts
    fetch("http://localhost:8000/alerts")
      .then((res) => res.json())
      .then((data) => setAlerts(data.alerts || []))
      .catch((err) => console.error("Failed to fetch alerts:", err));

    // Fetch orders
    fetch("http://localhost:8000/orders")
      .then((res) => res.json())
      .then((data) => setOrders(data))
      .catch((err) => console.error("Failed to fetch orders:", err));

    // Fetch inventory
    fetch("http://localhost:8000/inventory/analyze")
      .then((res) => res.json())
      .then((data) => setInventory(data.low_stock_items || []))
      .catch((err) => console.error("Failed to fetch inventory:", err));

    // Fetch transactions
    fetch("http://localhost:8000/transactions")
      .then((res) => res.json())
      .then((data) => setTransactions(data))
      .catch((err) => console.error("Failed to fetch transactions:", err));
  }, []);

  if (!dashboardData) return <p>Loading...</p>;

  // Data for the bar chart
  const barData = {
    labels: ["Inventory Items", "Transactions", "Orders"],
    datasets: [
      {
        label: "Counts",
        data: [
          dashboardData.total_inventory_items,
          dashboardData.total_transactions,
          dashboardData.total_orders,
        ],
        backgroundColor: ["#4caf50", "#2196f3", "#ff9800"],
      },
    ],
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Inventory Management Dashboard</h1>

      {/* Cards for displaying totals */}
      <div className="grid grid-cols-3 gap-4">
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Inventory Items</h2>
            <p className="text-2xl">{dashboardData.total_inventory_items}</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Transactions</h2>
            <p className="text-2xl">{dashboardData.total_transactions}</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Orders</h2>
            <p className="text-2xl">{dashboardData.total_orders}</p>
          </CardContent>
        </Card>
      </div>

      {/* Bar chart for overview */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Overview</h2>
        <Bar data={barData} options={{ responsive: true }} />
      </div>

      {/* Alerts section */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Alerts</h2>
        {alerts.length > 0 ? (
          <ul className="list-disc list-inside">
            {alerts.map((alert, index) => (
              <li key={index}>{alert}</li>
            ))}
          </ul>
        ) : (
          <p>No alerts</p>
        )}
      </div>

      {/* Recent orders table */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Recent Orders</h2>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Customer</TableCell>
              <TableCell>Product Code</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <TableRow key={order.id}>
                  <TableCell>{order.customer_name}</TableCell>
                  <TableCell>{order.product_code}</TableCell>
                  <TableCell>{order.quantity}</TableCell>
                  <TableCell>{order.status}</TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={4} className="text-center">
                  No orders available
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {/* All Inventory Table */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">All Inventory</h2>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Product Code</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Price</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {inventory.length > 0 ? (
              inventory.map((item) => (
                <TableRow key={item.id}>
                  <TableCell>{item.product_code}</TableCell>
                  <TableCell>{item.name}</TableCell>
                  <TableCell>{item.category}</TableCell>
                  <TableCell>{item.quantity}</TableCell>
                  <TableCell>{item.price}</TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={5} className="text-center">
                  No inventory data available
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {/* All Transactions Table */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">All Transactions</h2>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Product Code</TableCell>
              <TableCell>Change</TableCell>
              <TableCell>Transaction Type</TableCell>
              <TableCell>Timestamp</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {transactions.length > 0 ? (
              transactions.map((transaction) => (
                <TableRow key={transaction.id}>
                  <TableCell>{transaction.product_code}</TableCell>
                  <TableCell>{transaction.change}</TableCell>
                  <TableCell>{transaction.transaction_type}</TableCell>
                  <TableCell>{transaction.timestamp}</TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={4} className="text-center">
                  No transactions available
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}
