import React, { useEffect, useState } from "react";
import { Card, CardContent } from "../src/components/ui/card.js";
import { Bar } from "react-chartjs-2";
import { Table, TableHead, TableRow, TableCell, TableBody } from "../src/components/ui/table.js";

export default function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all data simultaneously
        const [dashboardResponse, alertsResponse, ordersResponse] = await Promise.all([
          fetch("http://localhost:5005/dashboard"),
          fetch("http://localhost:5005/alerts"),
          fetch("http://localhost:5005/orders"),
        ]);

        // Check if responses are ok
        if (!dashboardResponse.ok) throw new Error("Failed to fetch dashboard data");
        if (!alertsResponse.ok) throw new Error("Failed to fetch alerts");
        if (!ordersResponse.ok) throw new Error("Failed to fetch orders");

        // Parse JSON data
        const dashboardData = await dashboardResponse.json();
        const alertsData = await alertsResponse.json();
        const ordersData = await ordersResponse.json();

        // Validate and set data
        if (
          dashboardData &&
          typeof dashboardData.total_inventory_items === "number" &&
          typeof dashboardData.total_transactions === "number" &&
          typeof dashboardData.total_orders === "number"
        ) {
          setDashboardData(dashboardData);
        } else {
          throw new Error("Invalid dashboard data structure");
        }

        if (alertsData && Array.isArray(alertsData.alerts)) {
          setAlerts(alertsData.alerts);
        } else {
          throw new Error("Invalid alerts data structure");
        }

        if (ordersData && Array.isArray(ordersData)) {
          setOrders(ordersData);
        } else {
          throw new Error("Invalid orders data structure");
        }

        setIsLoading(false);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError(err.message);
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p style={{ color: "red" }}>Error: {error}</p>;
  }

  if (!dashboardData) {
    return <p>No data available</p>;
  }

  // Data for the bar chart
  const barData = {
    labels: ["Inventory Items", "Transactions", "Orders"],
    datasets: [
      {
        label: "Counts",
        data: [
          dashboardData.total_inventory_items,
          dashboardData.total_transactions,
          dashboardData.total_orders,
        ],
        backgroundColor: ["#4caf50", "#2196f3", "#ff9800"],
      },
    ],
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Inventory Management Dashboard</h1>

      {/* Cards for displaying totals */}
      <div className="grid grid-cols-3 gap-4">
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Inventory Items</h2>
            <p className="text-2xl">{dashboardData.total_inventory_items}</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Transactions</h2>
            <p className="text-2xl">{dashboardData.total_transactions}</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Orders</h2>
            <p className="text-2xl">{dashboardData.total_orders}</p>
          </CardContent>
        </Card>
      </div>

      {/* Bar chart for overview */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Overview</h2>
        <Bar data={barData} />
      </div>

      {/* Alerts section */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Alerts</h2>
        {alerts.length > 0 ? (
          <ul className="list-disc list-inside">
            {alerts.map((alert, index) => (
              <li key={index}>{alert}</li>
            ))}
          </ul>
        ) : (
          <p>No alerts</p>
        )}
      </div>

      {/* Recent orders table */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Recent Orders</h2>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Customer</TableCell>
              <TableCell>Product Code</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <TableRow key={order.id}>
                  <TableCell>{order.customer_name}</TableCell>
                  <TableCell>{order.product_code}</TableCell>
                  <TableCell>{order.quantity}</TableCell>
                  <TableCell>{order.status}</TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={4} className="text-center">
                  No orders available
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}
