import React, { useEffect, useState } from "react";
import { Card, CardContent } from "../src/components/ui/card.js";
import { Chart, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar } from "react-chartjs-2";
import { Table, TableHead, TableRow, TableCell, TableBody } from "../src/components/ui/table.js";

// ثبت مقیاس‌های مورد نیاز
Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [inventoryForm, setInventoryForm] = useState({
    product_code: "",
    name: "",
    category: "",
    quantity: 0,
    price: 0.0,
  });
  const [transactionForm, setTransactionForm] = useState({
    product_code: "",
    change: 0,
    transaction_type: "",
    timestamp: "",
  });
  const [uciData, setUciData] = useState([]); // State برای داده‌های UCI

  useEffect(() => {
    // Fetch dashboard data
    fetch("http://localhost:8000/dashboard")
      .then((res) => res.json())
      .then((data) => setDashboardData(data))
      .catch((err) => console.error("Failed to fetch dashboard data:", err));

    // Fetch alerts
    fetch("http://localhost:8000/alerts")
      .then((res) => res.json())
      .then((data) => setAlerts(data.alerts || []))
      .catch((err) => console.error("Failed to fetch alerts:", err));

    // Fetch orders
    fetch("http://localhost:8000/orders")
      .then((res) => res.json())
      .then((data) => setOrders(data))
      .catch((err) => console.error("Failed to fetch orders:", err));

    // Fetch inventory
    fetch("http://localhost:8000/inventory/analyze")
      .then((res) => res.json())
      .then((data) => setInventory(data.low_stock_items || []))
      .catch((err) => console.error("Failed to fetch inventory:", err));

    // Fetch transactions
    fetch("http://localhost:8000/transactions")
      .then((res) => res.json())
      .then((data) => setTransactions(data))
      .catch((err) => console.error("Failed to fetch transactions:", err));

    // Fetch UCI data
    fetch("http://localhost:8000/data")
      .then((res) => res.json())
      .then((data) => setUciData(data.data || []))
      .catch((err) => console.error("Failed to fetch UCI data:", err));
  }, []);

  const handleInventorySubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/inventory/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(inventoryForm),
      });
      if (response.ok) {
        alert("Inventory item added successfully!");
        setInventoryForm({
          product_code: "",
          name: "",
          category: "",
          quantity: 0,
          price: 0.0,
        });
      } else {
        alert("Failed to add inventory item.");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleTransactionSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/transactions/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(transactionForm),
      });
      if (response.ok) {
        alert("Transaction recorded successfully!");
        setTransactionForm({
          product_code: "",
          change: 0,
          transaction_type: "",
          timestamp: "",
        });
      } else {
        alert("Failed to record transaction.");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  if (!dashboardData) return <p>Loading...</p>;

  // Data for the bar chart
  const barData = {
    labels: ["Inventory Items", "Transactions", "Orders"],
    datasets: [
      {
        label: "Counts",
        data: [
          dashboardData.total_inventory_items,
          dashboardData.total_transactions,
          dashboardData.total_orders,
        ],
        backgroundColor: ["#4caf50", "#2196f3", "#ff9800"],
      },
    ],
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Inventory Management Dashboard</h1>

      {/* Cards for displaying totals */}
      <div className="grid grid-cols-3 gap-4">
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Inventory Items</h2>
            <p className="text-2xl">{dashboardData.total_inventory_items}</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Transactions</h2>
            <p className="text-2xl">{dashboardData.total_transactions}</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent>
            <h2 className="text-lg font-semibold">Total Orders</h2>
            <p className="text-2xl">{dashboardData.total_orders}</p>
          </CardContent>
        </Card>
      </div>

      {/* Bar chart for overview */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Overview</h2>
        <Bar data={barData} options={{ responsive: true }} />
      </div>

      {/* Alerts section */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Alerts</h2>
        {alerts.length > 0 ? (
          <ul className="list-disc list-inside">
            {alerts.map((alert, index) => (
              <li key={index}>{alert}</li>
            ))}
          </ul>
        ) : (
          <p>No alerts</p>
        )}
      </div>

      {/* Recent orders table */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Recent Orders</h2>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Customer</TableCell>
              <TableCell>Product Code</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <TableRow key={order.id}>
                  <TableCell>{order.customer_name}</TableCell>
                  <TableCell>{order.product_code}</TableCell>
                  <TableCell>{order.quantity}</TableCell>
                  <TableCell>{order.status}</TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={4} className="text-center">
                  No orders available
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {/* Add Inventory Form */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Add Inventory Item</h2>
        <form onSubmit={handleInventorySubmit}>
          <div className="grid grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="Product Code"
              value={inventoryForm.product_code}
              onChange={(e) =>
                setInventoryForm({ ...inventoryForm, product_code: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="Name"
              value={inventoryForm.name}
              onChange={(e) =>
                setInventoryForm({ ...inventoryForm, name: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="Category"
              value={inventoryForm.category}
              onChange={(e) =>
                setInventoryForm({ ...inventoryForm, category: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={inventoryForm.quantity}
              onChange={(e) =>
                setInventoryForm({ ...inventoryForm, quantity: parseInt(e.target.value) })
              }
              required
            />
            <input
              type="number"
              placeholder="Price"
              value={inventoryForm.price}
              onChange={(e) =>
                setInventoryForm({ ...inventoryForm, price: parseFloat(e.target.value) })
              }
              required
            />
          </div>
          <button type="submit" className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
            Add Inventory Item
          </button>
        </form>
      </div>

      {/* Add Transaction Form */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Add Transaction</h2>
        <form onSubmit={handleTransactionSubmit}>
          <div className="grid grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="Product Code"
              value={transactionForm.product_code}
              onChange={(e) =>
                setTransactionForm({ ...transactionForm, product_code: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Change"
              value={transactionForm.change}
              onChange={(e) =>
                setTransactionForm({ ...transactionForm, change: parseInt(e.target.value) })
              }
              required
            />
            <input
              type="text"
              placeholder="Transaction Type"
              value={transactionForm.transaction_type}
              onChange={(e) =>
                setTransactionForm({ ...transactionForm, transaction_type: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="Timestamp"
              value={transactionForm.timestamp}
              onChange={(e) =>
                setTransactionForm({ ...transactionForm, timestamp: e.target.value })
              }
              required
            />
          </div>
          <button type="submit" className="mt-4 bg-green-500 text-white px-4 py-2 rounded">
            Add Transaction
          </button>
        </form>
      </div>

      {/* All Data Section */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">All Data</h2>

        {/* All Inventory Table */}
        <div className="mt-4">
          <h3 className="text-md font-semibold">Inventory</h3>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Product Code</TableCell>
                <TableCell>Name</TableCell>
                <TableCell>Category</TableCell>
                <TableCell>Quantity</TableCell>
                <TableCell>Price</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {inventory.length > 0 ? (
                inventory.map((item) => (
                  <TableRow key={item.id}>
                    <TableCell>{item.product_code}</TableCell>
                    <TableCell>{item.name}</TableCell>
                    <TableCell>{item.category}</TableCell>
                    <TableCell>{item.quantity}</TableCell>
                    <TableCell>{item.price}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={5} className="text-center">
                    No inventory data available
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>

        {/* All Transactions Table */}
        <div className="mt-4">
          <h3 className="text-md font-semibold">Transactions</h3>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Product Code</TableCell>
                <TableCell>Change</TableCell>
                <TableCell>Transaction Type</TableCell>
                <TableCell>Timestamp</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {transactions.length > 0 ? (
                transactions.map((transaction) => (
                  <TableRow key={transaction.id}>
                    <TableCell>{transaction.product_code}</TableCell>
                    <TableCell>{transaction.change}</TableCell>
                    <TableCell>{transaction.transaction_type}</TableCell>
                    <TableCell>{transaction.timestamp}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={4} className="text-center">
                    No transactions available
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>

        {/* All Orders Table */}
        <div className="mt-4">
          <h3 className="text-md font-semibold">Orders</h3>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Customer</TableCell>
                <TableCell>Product Code</TableCell>
                <TableCell>Quantity</TableCell>
                <TableCell>Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {orders.length > 0 ? (
                orders.map((order) => (
                  <TableRow key={order.id}>
                    <TableCell>{order.customer_name}</TableCell>
                    <TableCell>{order.product_code}</TableCell>
                    <TableCell>{order.quantity}</TableCell>
                    <TableCell>{order.status}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={4} className="text-center">
                    No orders available
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>

        {/* UCI Data Table */}
        <div className="mt-4">
          <h3 className="text-md font-semibold">UCI Data</h3>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Sepal Length</TableCell>
                <TableCell>Sepal Width</TableCell>
                <TableCell>Petal Length</TableCell>
                <TableCell>Petal Width</TableCell>
                <TableCell>Species</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {uciData.length > 0 ? (
                uciData.map((item, index) => (
                  <TableRow key={index}>
                    <TableCell>{item.sepal_length}</TableCell>
                    <TableCell>{item.sepal_width}</TableCell>
                    <TableCell>{item.petal_length}</TableCell>
                    <TableCell>{item.petal_width}</TableCell>
                    <TableCell>{item.species}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={5} className="text-center">
                    No UCI data available
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>
      </div>
    </div>
  );
}
